function transform (message, context) {
  const { payload: contact } = message;

  const { '__user': user, testPersonId } = context;
  const personId = testPersonId || user?.id;

  context.set('contact', contact);
  context.set('personId', personId);

  return message;
}

function transform (message, context) {
  const { payload: { personEmergencyContact } = {} } = message;
  const { personId } = context;

  if ( personEmergencyContact.person.id !== personId ) {
    const message = 'User it not allows to update this resource record';
    context.set('__httpStatusMessage', message);
    context.set('__httpStatusCode', 403);
    throw new Error(message);
  }

  // success
  return message;
}

function transform (message, context) {
  const { contact } = context;

  // Zero out the ID
  contact.id = '00000000-0000-0000-0000-000000000000';

  return {
    payload: contact
  };
}
